//分享api；有需求的照着添加
//api项，请返回给一个{url:url,wSize:{w:,h:}}，脚本会自动调用window.open打开，如果不返回任何的话，脚本将不做任何其他事情。
//api的参数
/*{
	title
	pic
	url
} */
prefs.share={
	weibo:{
		disabled:false,
		name:'新浪微博',
		icon:'data:image/png;base64,',
		api:function(args){
			var url='http://service.weibo.com/share/share.php?'+
				'title='+args.title+
				'&url='+args.url+
				'&pic='+args.pic;
			return {
				url:url,
				wSize:{
					h:500,
					w:620,
				},
			};
		},
	},
	t:{
		name:'腾讯微博',
		icon:'data:image/png;base64,',
		api:function(args){
			var url='http://v.t.qq.com/share/share.php?'+
			'title='+args.title+
			'&url='+args.url+
			'&pic='+args.pic;
			return {
				url:url,
				wSize:{
					h:500,
					w:620,
				},
			};
		},
	},
	qZone:{
		name:'QQ空间',
		icon:'data:image/png;base64,',
		api:function(args){
			var url='http://sns.qzone.qq.com/cgi-bin/qzshare/cgi_qzshare_onekey?'+
				'title='+args.title+
				'&pics='+args.pic+
				'&url='+args.url;
				return {
					url:url,
					wSize:{
						h:650,
						w:620,
					},
				};
		},
	},
	fanfou:{
		name:'饭否',
		icon:'data:image/png;base64,',
		api:function(args){
			var url='http://fanfou.com/sharer/image?'+
			'u='+args.url+
			'&t='+args.title+
			'&img_src='+args.pic;
			return{
				url:url,
				wSize:{
					h:550,
					w:650,
				},
			};
		},
	},
	tieba:{
		name:'百度贴吧',
		icon:'data:image/png;base64,',
		api:function(args){
			var url = 'http://tieba.baidu.com/f/commit/share/openShareApi?'+
			'title='+args.title+
			'&url='+args.url+
			'&pic='+args.pic;
			return {
				url:url,
				wSize:{
					h:600,
					w:630,
				},
			};
		},
	},
	renren:{
		name:'人人网',
		icon:'data:image/png;base64,',
		api:function(args){
			var url='http://widget.renren.com/dialog/share?'+
			'link='+args.url+
			'&title='+args.title+
			'&pic='+args.pic;
			return {
				url:url,
				wSize:{
					h:600,
					w:650,
				},
			};
		},
	},
	douban:{
		name:'豆瓣',
		icon:'data:image/png;base64,',
		api:function(args){
			var url='http://shuo.douban.com/%21service/share?'+
			'href='+args.url+
			'&name='+args.title+
			'&image='+args.pic;
			return {
				url:url,
				wSize:{
					h:350,
					w:600,
				},
			};
		},
	},
};
